import React, { useState, useRef, useEffect } from 'react';
// Ensure the path to your CSS module is correct
import styles from './index-react.module.css';
import { useNavigate } from 'react-router-dom';
// Ensure the path to your API client is correct
import { callApi } from '../api/client'; // Adjust path if necessary

// Import MUI Icons (assuming you have @mui/material and @mui/icons-material installed)
import AttachFileIcon from '@mui/icons-material/AttachFile';
import MicIcon from '@mui/icons-material/Mic';

export default function Home() {
  const [queryInput, setQueryInput] = useState('');
  const [file, setFile] = useState(null); // State to hold the selected file object
  const [fileName, setFileName] = useState(''); // State to hold the file name for display
  const navigate = useNavigate();
  const inputRef = useRef(null);
  const [theme] = useState('dark'); // Keep theme state if used elsewhere

  const recognitionRef = useRef(null);
  const [isListening, setIsListening] = useState(false);
  const [micError, setMicError] = useState('');
  const [micReady, setMicReady] = useState(false);
  const [isLoading, setIsLoading] = useState(false); // State for loading indicator
  const [apiError, setApiError] = useState(''); // State for API errors

  // --- Speech Recognition Setup (Keep As-Is) ---
  useEffect(() => {
    const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
    if (!SpeechRecognition) {
      setMicError("Voice input not supported by this browser.");
      setMicReady(false); return;
    }
    if (navigator.permissions && typeof navigator.permissions.query === 'function') {
      navigator.permissions.query({ name: 'microphone' }).then((ps) => {
        if (ps.state === 'denied') { setMicError("Mic permission denied."); setMicReady(false); }
        ps.onchange = () => { if (ps.state === 'denied') { setMicError("Mic permission denied."); setMicReady(false); recognitionRef.current?.stop(); } else { setMicError(''); } };
      }).catch(err => console.error("Mic permission query error:", err));
    }
    const recognition = new SpeechRecognition();
    recognition.lang = "en-US"; recognition.interimResults = false; recognition.maxAlternatives = 1;
    recognition.onresult = (e) => { setQueryInput(e.results[0][0].transcript); };
    recognition.onerror = (e) => {
      let msg = `Speech error: ${e.error}`;
      if (e.error === 'not-allowed' || e.error === 'service-not-allowed') { msg = "Mic permission denied."; }
      else if (e.error === 'no-speech') { msg = "No speech detected."; }
      else if (e.error === 'audio-capture') { msg = "Mic hardware error."; }
      else if (e.error === 'network') { msg = "Network error during speech recognition."; }
      setMicError(msg); setIsListening(false);
    };
    recognition.onstart = () => { setIsListening(true); setMicError(''); setApiError('');}; // Clear errors on start
    recognition.onend = () => { setIsListening(false); };
    recognitionRef.current = recognition; setMicReady(true); setMicError('');
    return () => { recognitionRef.current?.abort(); };
  }, []);

  // --- File Handling ---
  const handleFileChange = (e) => {
    setApiError(''); // Clear previous API errors
    if (e.target.files && e.target.files[0]) {
        const selectedFile = e.target.files[0];
        console.log("DEBUG: File selected:", selectedFile.name, selectedFile.type, selectedFile.size); // <-- DEBUG LOG
        setFile(selectedFile); // Store the actual File object
        setFileName(selectedFile.name); // Store the name for display
        setMicError('');
    } else {
        console.log("DEBUG: No file selected or selection cancelled."); // <-- DEBUG LOG
        setFile(null);
        setFileName('');
    }
    // Reset the input value so the same file can be selected again if needed
    e.target.value = null;
  };

  // --- Form Submission ---
  const handleSubmit = async (e) => {
    e.preventDefault();
    setApiError(''); // Clear previous errors
    if (!queryInput && !file) {
        setApiError("Please enter a prompt or upload a file.");
        return;
    }
    if (isListening && recognitionRef.current) {
      recognitionRef.current.stop();
      // Maybe wait a fraction of a second if needed, but usually stopping is enough
    }
    setIsLoading(true); // Start loading indicator

    const formData = new FormData();
    formData.append('queryInput', queryInput);

    console.log("DEBUG: handleSubmit - File state before append:", file); // <-- DEBUG LOG
    if (file) {
        // --- CRITICAL: Use the key 'file-upload' ---
        formData.append('file-upload', file, file.name); // Pass file object and its name
        console.log("DEBUG: handleSubmit - Appended file to FormData with key 'file-upload':", file.name); // <-- DEBUG LOG
    } else {
        console.log("DEBUG: handleSubmit - No file to append."); // <-- DEBUG LOG
    }

    // Optional: Log FormData keys (won't show file details but confirms keys)
    for (let key of formData.keys()) {
       console.log("DEBUG: FormData key being sent:", key);
    }

    try {
        // --- CRITICAL: Ensure callApi handles FormData correctly ---
        const data = await callApi('home', 'POST', formData);
        console.log("DEBUG: API Response Data:", data); // <-- DEBUG LOG

        if (data && data.conversation) {
            // Navigate to conversation page on success
            navigate('/conversation', { state: { conversation: data.conversation, filedetails: data.filedetails, final_problem: data.final_problem } });
        } else if (data && data.error) {
            // Handle specific errors returned from backend
            console.error("API Error Response:", data.error, data.details);
            setApiError(`Server error: ${data.error}${data.details ? ` (${data.details})` : ''}`);
        }
         else {
            // Handle unexpected responses
            console.error("API response missing expected data (conversation or error). Response:", data);
            setApiError("Received an unexpected response from the server.");
        }
    } catch (error) {
        // Handle network errors or errors thrown by callApi
        console.error("API call failed:", error);
        setApiError(error.message || "Network error or server unavailable.");
    } finally {
        setIsLoading(false); // Stop loading indicator
    }
  };

  // --- Voice Input Handling (Keep As-Is) ---
  const handleVoiceInput = () => {
    if (!micReady || !recognitionRef.current) { if (!micError) setMicError("Voice input unavailable or not ready."); return; }
    if (!isListening) {
      try { setQueryInput(''); setFile(null); setFileName(''); setMicError(''); setApiError(''); recognitionRef.current.start(); } // Clear file on mic start
      catch (error) { console.error("Mic start error:", error); setMicError("Could not start mic."); setIsListening(false); }
    } else { recognitionRef.current.stop(); }
  };

  const micButtonDisabled = !micReady;

  // Construct className strings using the imported styles object
  const autoMLClassName = `${styles.autoML || ''} ${isListening ? styles.listening : ''}`.trim();
  const micButtonClassName = `${styles.iconButton || ''} ${isListening ? styles.active : ''}`.trim();

  return (
    <div className={autoMLClassName}>
      {/* Assume CSS module defines .container */}
      <div className={styles.container}>
        {/* Assume CSS module defines .header */}
        <div className={styles.header}>
          <p>Bonjour</p>
          <h1>How Can We Help You?</h1>
        </div>

        <form onSubmit={handleSubmit}>
          {/* Assume CSS module defines .inputArea */}
          <div className={styles.inputArea}>
            {/* Assume CSS module defines .inputWrapper */}
            <div className={styles.inputWrapper}>
              <input
                type="text"
                ref={inputRef}
                value={queryInput}
                onChange={(e) => { setQueryInput(e.target.value); setApiError(''); }} // Clear API error on typing
                placeholder={isListening ? "Listening..." : "Describe Your Problem or Upload CSV"}
                // Removed 'required' as either text OR file is needed
                disabled={isListening || isLoading} // Disable when loading too
              />
              {/* Assume CSS module defines .inputIcons */}
              <div className={styles.inputIcons}>
                <label
                  htmlFor="file-upload-input" // Use unique ID for input
                  className={styles.iconButton}
                  aria-disabled={isListening || isLoading}
                  title="Attach CSV file"
                  style={(isListening || isLoading) ? { pointerEvents: 'none', opacity: 0.5 } : {}}
                >
                  <AttachFileIcon fontSize="inherit" />
                </label>
                <input
                  id="file-upload-input" // Match label's htmlFor
                  type="file"
                  accept=".csv" // Specify accepted file type
                  hidden
                  onChange={handleFileChange}
                  disabled={isListening || isLoading}
                />
                <button
                  type="button"
                  onClick={handleVoiceInput}
                  className={micButtonClassName}
                  disabled={micButtonDisabled || isLoading} // Disable mic when loading
                  title={micError ? micError : !micReady ? "Voice input unavailable" : isListening ? "Stop Listening" : "Start Voice Input"}
                >
                  <MicIcon fontSize="inherit" />
                </button>
              </div>
            </div>

            {/* Status Messages Area */}
            <div className={styles.statusArea}>
                {fileName && !micError && !isListening && !apiError && (
                  <p className={styles.fileDisplay}>Selected File: {fileName}</p>
                )}
                {isListening && (
                  <p className={`${styles.statusDisplay || ''} ${styles.listeningIndicator || ''}`.trim()}>🎙️ Listening...</p>
                )}
                {micError && (
                  <p className={`${styles.statusDisplay || ''} ${styles.errorMessage || ''}`.trim()}>⚠️ Mic Error: {micError}</p>
                )}
                {apiError && (
                  <p className={`${styles.statusDisplay || ''} ${styles.errorMessage || ''}`.trim()}>❌ Error: {apiError}</p>
                )}
             </div>
          </div>

          <button type="submit" className={styles.actionButton} disabled={isListening || isLoading}>
            {isLoading ? 'Processing...' : 'Start Analysis'}
          </button>
        </form>
      </div>
    </div>
  );
}
