import React, { useEffect, useState, useRef } from 'react';
import { Box, List, ListItem, ListItemText, TextField, Button, Paper, Typography, InputAdornment, IconButton } from '@mui/material';
import { useLocation, useNavigate } from 'react-router-dom';
import SendIcon from '@mui/icons-material/Send';
import { callApi } from '../api/client';

const parseMessage = (msg) => {
  if (typeof msg !== 'string') return { sender: 'unknown', content: String(msg) };
  const parts = msg.split(":", 1);
  const sender = parts[0].toLowerCase();
  let content = msg.substring(parts[0].length + 1).trim();
  if (sender === 'ai' && content.match(/^Question\s*\d+\)/i)) {
    content = content.substring(content.indexOf(')') + 1).trim();
  }
  return { sender, content };
};

export default function Conversation() {
  const { state } = useLocation();
  const navigate = useNavigate();
  const [conversation, setConversation] = useState(state?.conversation || []);
  const [userResponse, setUserResponse] = useState('');
  const [isFinal, setIsFinal] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const conversationEndRef = useRef(null);

  useEffect(() => {
    conversationEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [conversation]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!userResponse.trim() || isLoading) return;
    setIsLoading(true);
    const messageToSend = userResponse;
    setUserResponse('');
    try {
      const data = await callApi('conversation', 'POST', { user_response: messageToSend });
      setConversation(data.conversation || []);
      if (data.final_problem) {
        setIsFinal(true);
      }
    } catch (error) {
      console.error("API call failed:", error);
    } finally {
       setIsLoading(false);
    }
  };

  return (
    <Box sx={{
      maxWidth: '700px',
      margin: 'auto',
      mt: 4,
      mb: 4,
      display: 'flex',
      flexDirection: 'column',
      height: 'calc(100vh - 64px)',
      overflow: 'hidden',
      border: '1px solid',
      borderColor: 'divider',
      borderRadius: '8px',
      boxShadow: 3,
      bgcolor: 'background.paper'
    }}>
      <Box sx={{ p: 2, borderBottom: '1px solid', borderColor: 'divider' }}>
        <Typography variant="h5" component="h1" align="center" gutterBottom>
          Getting On Track...
        </Typography>
      </Box>
      <List sx={{
          flexGrow: 1,
          overflowY: 'auto',
          p: 2,
          display: 'flex',
          flexDirection: 'column',
          gap: 1,
      }}>
        {conversation.map((msg, i) => {
          const { sender, content } = parseMessage(msg);
          const isAI = sender === 'ai';
          return (
            <ListItem
              key={i}
              sx={{
                display: 'flex',
                justifyContent: isAI ? 'flex-start' : 'flex-end',
                p: 0,
              }}
            >
              <Paper
                elevation={1}
                sx={{
                  p: 1.5,
                  borderRadius: '16px',
                  borderTopLeftRadius: isAI ? '4px' : '16px',
                  borderTopRightRadius: isAI ? '16px' : '4px',
                  bgcolor: isAI ? 'primary.main' : 'secondary.main',
                  color: isAI ? 'primary.contrastText' : 'secondary.contrastText',
                  maxWidth: '75%',
                }}
              >
                <ListItemText
                    primary={content}
                    primaryTypographyProps={{ sx: { wordBreak: 'break-word' } }}
                />
              </Paper>
            </ListItem>
          );
        })}
        <div ref={conversationEndRef} />
      </List>
      <Box sx={{ p: 2, borderTop: '1px solid', borderColor: 'divider', bgcolor: 'background.default' }}>
        {isFinal ? (
          <Typography align="center" color="text.secondary">
            Your final business problem has been saved for later use.
          </Typography>
        ) : (
          <Box component="form" onSubmit={handleSubmit} sx={{ display: 'flex', gap: 1 }}>
            <TextField
              fullWidth
              variant="outlined"
              size="small"
              placeholder="Type your answer here..."
              value={userResponse}
              onChange={e => setUserResponse(e.target.value)}
              required
              disabled={isLoading}
              sx={{
                  '& .MuiOutlinedInput-root': {
                    borderRadius: '20px',
                  },
               }}
               InputProps={{
                 endAdornment: (
                    <InputAdornment position="end">
                      <IconButton
                        type="submit"
                        color="primary"
                        disabled={isLoading || !userResponse.trim()}
                        edge="end"
                      >
                        <SendIcon />
                      </IconButton>
                    </InputAdornment>
                  ),
               }}
            />
          </Box>
        )}
      </Box>
    </Box>
  );
}
