// dashboard.js
import React, { useEffect, useState } from 'react';
import styles from './dashboard.module.css';

export default function Dashboard({ vizPath = '/viz' }) {
  const [explanations, setExplanations] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const jsonUrl = `${vizPath}/plot_explanations.json`;
    fetch(jsonUrl)
      .then(res => {
        if (!res.ok) throw new Error(`HTTP ${res.status} – ${res.statusText}`);
        return res.json();
      })
      .then(data => {
        setExplanations(data);
        setLoading(false);
      })
      .catch(err => {
        console.error('Failed to load explanations JSON:', err);
        setError('Unable to load plot explanations.');
        setLoading(false);
      });
  }, [vizPath]);

  if (loading) {
    return <p className={styles.loading}>Loading visualizations…</p>;
  }

  if (error) {
    return <p className={styles.error}>{error}</p>;
  }

  const entries = Object.entries(explanations || {});
  if (entries.length === 0) {
    return <p className={styles.loading}>No explanations found.</p>;
  }

  return (
    <div className={styles.container}>
      <div className={styles.grid}>
        {entries.map(([filename, explanation]) => {
          const imgSrc = `${vizPath}/${filename}`;
          return (
            <div key={filename} className={styles.card}>
              <img
                src={imgSrc}
                alt={filename}
                className={styles.image}
              />
              <div className={styles.explanation}>
                {explanation}
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
}
