// src/components/Home.js (or similar)

import React, { useState, useRef, useEffect } from 'react';
import styles from './index-react.module.css'; // Ensure path is correct
import { useNavigate } from 'react-router-dom';
import { callApi } from '../api/client'; // Ensure path is correct
import AttachFileIcon from '@mui/icons-material/AttachFile';
import MicIcon from '@mui/icons-material/Mic';

export default function Home() {
  const [queryInput, setQueryInput] = useState('');
  const [file, setFile] = useState(null);
  const [fileName, setFileName] = useState('');
  const navigate = useNavigate();
  const inputRef = useRef(null);
  const [theme] = useState('dark');
  const recognitionRef = useRef(null);
  const [isListening, setIsListening] = useState(false);
  const [micError, setMicError] = useState('');
  const [micReady, setMicReady] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [apiError, setApiError] = useState('');

  // useEffect for speech recognition (keep as is)
  useEffect(() => {
    // ... (your existing useEffect code) ...
  }, []);

  // handleFileChange (keep as is, but ensure console logs are present for debugging)
  const handleFileChange = (e) => {
    setApiError('');
    if (e.target.files && e.target.files[0]) {
        const selectedFile = e.target.files[0];
        console.log("DEBUG: File selected:", selectedFile.name, selectedFile.type, selectedFile.size);
        setFile(selectedFile);
        setFileName(selectedFile.name);
        setMicError('');
    } else {
        console.log("DEBUG: No file selected or selection cancelled.");
        setFile(null);
        setFileName('');
    }
    e.target.value = null;
  };

  // handleSubmit - REVISED with more logging and clearer error handling
  const handleSubmit = async (e) => {
    e.preventDefault();
    setApiError('');
    if (!queryInput && !file) {
        setApiError("Please enter a prompt or upload a file.");
        return;
    }
    if (isListening && recognitionRef.current) {
      recognitionRef.current.stop();
    }
    setIsLoading(true);
    console.log("DEBUG: handleSubmit triggered. isLoading: true"); // Log start

    const formData = new FormData();
    formData.append('queryInput', queryInput);

    if (file) {
        formData.append('file-upload', file, file.name); // Use correct key 'file-upload'
        console.log("DEBUG: Appended file to FormData:", file.name);
    } else {
        console.log("DEBUG: No file to append.");
    }

    try {
        console.log("DEBUG: Calling API endpoint '/api/home'...");
        const data = await callApi('home', 'POST', formData); // Ensure callApi is correctly implemented
        console.log("DEBUG: Received API response data:", data); // Log the *entire* response

        // --- Check response structure rigorously ---
        if (data && typeof data === 'object') {
            if (data.conversation && Array.isArray(data.conversation)) {
                // SUCCESS CASE - Navigate
                console.log("DEBUG: Success response received. Navigating to /conversation...");
                navigate('/conversation', {
                    state: {
                        conversation: data.conversation,
                        filedetails: data.filedetails, // Ensure backend sends this
                        final_problem: data.final_problem // Ensure backend sends this
                    }
                });
            } else if (data.error) {
                // Backend returned a structured error
                console.error("API returned an error:", data.error, data.details || '');
                setApiError(`Server error: ${data.error}${data.details ? ` (${data.details})` : ''}`);
            } else {
                // Backend returned OK status but unexpected JSON structure
                console.error("API response missing expected 'conversation' array or 'error' field. Response:", data);
                setApiError("Received an unexpected success response structure from the server.");
            }
        } else {
             // Backend returned non-JSON or malformed response
             console.error("API response was not a valid object:", data);
             setApiError("Received an invalid response format from the server.");
        }
    } catch (error) {
        // Catch errors from callApi (network, non-2xx status, etc.)
        console.error("Error during API call via callApi:", error);
        setApiError(error.message || "An unknown error occurred during the request.");
    } finally {
        setIsLoading(false); // Stop loading indicator regardless of outcome
        console.log("DEBUG: handleSubmit finished. isLoading: false"); // Log end
    }
  };

  // handleVoiceInput (keep as is)
  const handleVoiceInput = () => {
    if (!micReady || !recognitionRef.current) { if (!micError) setMicError("Voice input unavailable or not ready."); return; }
    if (!isListening) {
      try { setQueryInput(''); setFile(null); setFileName(''); setMicError(''); setApiError(''); recognitionRef.current.start(); }
      catch (error) { console.error("Mic start error:", error); setMicError("Could not start mic."); setIsListening(false); }
    } else { recognitionRef.current.stop(); }
  };


  const micButtonDisabled = !micReady;
  const autoMLClassName = `${styles.autoML || ''} ${isListening ? styles.listening : ''}`.trim();
  const micButtonClassName = `${styles.iconButton || ''} ${isListening ? styles.active : ''}`.trim();

  // --- JSX Rendering (Keep as is, but uses the state updated above) ---
  return (
    <div className={autoMLClassName}>
      <div className={styles.container}>
        <div className={styles.header}>
          <p>Bonjour</p>
          <h1>How Can We Help You?</h1>
        </div>

        <form onSubmit={handleSubmit}>
          <div className={styles.inputArea}>
            <div className={styles.inputWrapper}>
              <input
                type="text"
                ref={inputRef}
                value={queryInput}
                onChange={(e) => { setQueryInput(e.target.value); setApiError(''); }}
                placeholder={isListening ? "Listening..." : "Describe Your Problem or Upload CSV"}
                disabled={isListening || isLoading}
              />
              <div className={styles.inputIcons}>
                <label
                  htmlFor="file-upload-input"
                  className={styles.iconButton}
                  aria-disabled={isListening || isLoading}
                  title="Attach CSV file"
                  style={(isListening || isLoading) ? { pointerEvents: 'none', opacity: 0.5 } : {}}
                >
                  <AttachFileIcon fontSize="inherit" />
                </label>
                <input
                  id="file-upload-input"
                  type="file"
                  accept=".csv"
                  hidden
                  onChange={handleFileChange}
                  disabled={isListening || isLoading}
                />
                <button
                  type="button"
                  onClick={handleVoiceInput}
                  className={micButtonClassName}
                  disabled={micButtonDisabled || isLoading}
                  title={micError ? micError : !micReady ? "Voice input unavailable" : isListening ? "Stop Listening" : "Start Voice Input"}
                >
                  <MicIcon fontSize="inherit" />
                </button>
              </div>
            </div>

            {/* Status Messages Area */}
            <div className={styles.statusArea}>
                {/* Display selected file name */}
                {fileName && !apiError && !micError && ( // Show filename unless there's an error
                  <p className={styles.fileDisplay}>Selected File: {fileName}</p>
                )}
                {/* Display microphone status */}
                {isListening && (
                  <p className={`${styles.statusDisplay || ''} ${styles.listeningIndicator || ''}`.trim()}>üéôÔ∏è Listening...</p>
                )}
                {micError && (
                  <p className={`${styles.statusDisplay || ''} ${styles.errorMessage || ''}`.trim()}>‚ö†Ô∏è Mic Error: {micError}</p>
                )}
                {/* Display API/Submission errors */}
                {apiError && (
                  <p className={`${styles.statusDisplay || ''} ${styles.errorMessage || ''}`.trim()}>‚ùå Error: {apiError}</p>
                )}
             </div>
          </div>

          {/* Submit Button */}
          <button type="submit" className={styles.actionButton} disabled={isListening || isLoading}>
            {isLoading ? 'Processing...' : 'Start Analysis'}
          </button>
        </form>
      </div>
    </div>
  );
}
