import React, { useState, useRef, useEffect } from 'react';
import './index-react.css';
import { Button, IconButton, TextField } from '@mui/material';
import AttachFileIcon from '@mui/icons-material/AttachFile';
import MicIcon from '@mui/icons-material/Mic';
import DarkModeIcon from '@mui/icons-material/DarkMode';
import LightModeIcon from '@mui/icons-material/LightMode';

const AutoMLAssistant = () => {
  const [theme, setTheme] = useState('dark');
  const [query, setQuery] = useState('');
  const recognitionRef = useRef(null);

  useEffect(() => {
    const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
    if (SpeechRecognition) {
      const recognition = new SpeechRecognition();
      recognition.lang = 'en-US';
      recognition.interimResults = false;
      recognition.maxAlternatives = 1;
      recognition.onresult = (event) => {
        setQuery(event.results[0][0].transcript);
      };
      recognitionRef.current = recognition;
    }
  }, []);

  const handleVoice = () => {
    if (recognitionRef.current) recognitionRef.current.start();
  };

  const toggleTheme = () => {
    setTheme((prev) => (prev === 'dark' ? 'light' : 'dark'));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Submitted:', query);
  };

  return (
    <div className={`assistant-wrapper ${theme}`}>
      <div className="background-animation" />

      <IconButton className="theme-toggle" onClick={toggleTheme}>
        {theme === 'dark' ? <DarkModeIcon /> : <LightModeIcon />}
      </IconButton>

      <form className="assistant-form" onSubmit={handleSubmit}>
        <div className="input-container tilt">
          <TextField
            fullWidth
            variant="outlined"
            placeholder="What do you want to know?"
            value={query}
            onChange={(e) => setQuery(e.target.value)}
            InputProps={{
              style: {
                fontSize: '1.2rem',
                color: theme === 'dark' ? '#fff' : '#000',
              },
            }}
          />

          <div className="icons">
            <label htmlFor="file-upload" className="icon-btn">
              <AttachFileIcon />
            </label>
            <input id="file-upload" type="file" hidden />
            <IconButton className="icon-btn" onClick={handleVoice}>
              <MicIcon />
            </IconButton>
          </div>
        </div>

        <Button type="submit" variant="contained" className="submit-btn tilt">
          Let's GO
        </Button>
      </form>
    </div>
  );
};

export default AutoMLAssistant;
