import React, { useEffect, useState, useRef } from 'react';
import './conversation-style.css'; // Import the CSS file
import { useLocation, useNavigate } from 'react-router-dom';
import { callApi } from '../api/client';

const parseMessage = (msg) => {
  if (typeof msg !== 'string') return { sender: 'unknown', content: String(msg) };
  const parts = msg.split(":", 1);
  const sender = parts[0].toLowerCase();
  let content = msg.substring(parts[0].length + 1).trim();
  if (sender === 'ai' && content.match(/^Question\s*\d+\)/i)) {
    content = content.substring(content.indexOf(')') + 1).trim();
  }
  return { sender, content };
};

export default function Conversation() {
  const { state } = useLocation();
  const navigate = useNavigate();
  const [conversation, setConversation] = useState(state?.conversation || []);
  const [userResponse, setUserResponse] = useState('');
  const [isFinal, setIsFinal] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const conversationEndRef = useRef(null);

  useEffect(() => {
    conversationEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [conversation]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!userResponse.trim() || isLoading) return;
    setIsLoading(true);
    const messageToSend = userResponse;
    setUserResponse('');
    // Optimistically add user message (matches CSS structure)
    setConversation(prev => [...prev, `User: ${messageToSend}`]); // Ensure format matches parsing
    try {
      const data = await callApi('conversation', 'POST', { user_response: messageToSend });
      // API should return the full conversation including the user message and AI response
      setConversation(data.conversation || []);
      if (data.final_problem) {
        setIsFinal(true);
      }
    } catch (error) {
      console.error("API call failed:", error);
      // Optionally remove the optimistic message or show error
      setConversation(prev => prev.slice(0, -1)); // Remove optimistic message on error
      setUserResponse(messageToSend); // Put message back in input
    } finally {
       setIsLoading(false);
    }
  };

  return (
    <div className="chat-container">
      <div className="header">
        <h1>Getting On Track...</h1>
      </div>

      <div className="conversation">
        {conversation.map((msg, i) => {
          const { sender, content } = parseMessage(msg);
          const isAI = sender === 'ai';
          // Use className based on sender
          const bubbleClass = isAI ? 'ai-bubble' : 'user-bubble';
          return (
            <div key={i} className={bubbleClass}>
              <p>{content}</p>
            </div>
          );
        })}
        <div ref={conversationEndRef} />
      </div>

      {isFinal ? (
        <div className="saved-message">
          <p>Your final business problem has been saved for later use.</p>
        </div>
      ) : (
        <form className="response-form" onSubmit={handleSubmit}>
          <input
            type="text"
            placeholder="Type your answer here..."
            value={userResponse}
            onChange={(e) => setUserResponse(e.target.value)}
            required
            disabled={isLoading}
          />
          <button
            type="submit"
            className="submit-button"
            disabled={isLoading || !userResponse.trim()}
          >
            Send
          </button>
        </form>
      )}
    </div>
  );
}
