import React, { useState, useRef } from 'react';
import './index-react.css';
import { useNavigate } from 'react-router-dom';
import { IconButton } from '@mui/material';
import AttachFileIcon from '@mui/icons-material/AttachFile';
import { callApi } from '../api/client';

export default function Home() {
  const [queryInput, setQueryInput] = useState('');
  const [file, setFile] = useState(null);
  const navigate = useNavigate();
  const inputRef = useRef(null);
  const [theme] = useState('dark');

  const handleFileChange = (e) => {
    if (e.target.files && e.target.files[0]) {
      setFile(e.target.files[0]);
    } else {
      setFile(null);
    }
  };

  const handleSubmit = async e => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('queryInput', queryInput);
    if (file) {
      formData.append('file', file);
    }
    try {
      const data = await callApi('home', 'POST', formData);
      if (data && data.conversation) {
        navigate('/conversation', { state: { conversation: data.conversation } });
      } else {
        console.error("API response missing data.");
      }
    } catch (error) {
      console.error("API call failed:", error);
    }
  };

  return (
    <div className={`autoML ${theme}`}>
      <div className="background-animation" />
      <div className="container">
        <div className="header">
          <p>Welcome</p>
          <h1>How Can We Help You?</h1>
        </div>
        <form onSubmit={handleSubmit}>
          <div className="input-area">
            <div className="input-wrapper tilt">
              <input
                type="text"
                ref={inputRef}
                value={queryInput}
                onChange={e => setQueryInput(e.target.value)}
                placeholder="Describe Your Problem"
                required
              />
              <div className="input-icons">
                <IconButton component="label" htmlFor="file-upload" className="icon-button">
                  <AttachFileIcon />
                </IconButton>
                <input
                  id="file-upload"
                  type="file"
                  hidden
                  onChange={handleFileChange}
                />
              </div>
            </div>
            {file && <p style={{ textAlign: 'center', marginTop: '10px', fontSize: '0.9em', color: 'lightgrey'}}>File: {file.name}</p>}
          </div>
          <button type="submit" className="action-button tilt">
            Start Analysis
          </button>
        </form>
      </div>
    </div>
  );
}
