import React, { useEffect, useRef, useState } from 'react';
import { useLocation } from 'react-router-dom';
import styles from './conversation_style.module.css';
import { callApi } from '../api/client';

export default function Conversation() {
  const { state } = useLocation();
  const [conversation, setConversation] = useState([]);
  const [userResponse, setUserResponse] = useState('');
  const [isFinal, setIsFinal] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const conversationEndRef = useRef(null);

  useEffect(() => {
    conversationEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [conversation]);

  const filterAndSetConversation = (convoData) => {
      const filteredConvo = convoData?.filter(line => {
          return true;
      }) || [];
      setConversation(filteredConvo);
  };


  useEffect(() => {
    const fetchFirstQuestion = async () => {
       if (conversation.length === 0 && state?.conversation?.length === 2) {
           setIsLoading(true);
           try {
             const data = await callApi('conversation', 'GET');
             const initialFilteredConvo = data.conversation?.filter((line, index) => {
                 return !(index === 0 && line.startsWith('User:'));
             }) || [];
             setConversation(initialFilteredConvo);
             if (data.final_problem) setIsFinal(true);
           } catch (err) {
             console.error('Failed to load first question:', err);
           } finally {
             setIsLoading(false);
           }
       } else if (state?.conversation) {
           const initialFilteredConvo = state.conversation.filter((line, index) => {
               return !(index === 0 && line.startsWith('User:'));
           }) || [];
           if (state.final_problem) {
                setIsFinal(true);
           }
           if (conversation.length === 0 && !isLoading) {
                setConversation(initialFilteredConvo);
           }
       }
    };
    fetchFirstQuestion();
  }, [state]);


  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!userResponse.trim() || isLoading) return;

    const answer = userResponse.trim();
    const currentConvo = conversation;

    setUserResponse('');
    setIsLoading(true);

    const updatedConvoUI = [...currentConvo, `User: ${answer}`];
    setConversation(updatedConvoUI);

    try {
      const data = await callApi('conversation', 'POST', { user_response: answer });
      const newConvoFromBackend = data.conversation || [];

      const finalFilteredConvo = newConvoFromBackend.filter((line, i) => {
           return !(i === 0 && line.startsWith('User:'));
      });

      setConversation(finalFilteredConvo);
      if (data.final_problem) setIsFinal(true);

    } catch (err) {
      console.error('API call failed:', err);
      setConversation(currentConvo);
      setUserResponse(answer);
    } finally {
      setIsLoading(false);
    }
  };

  const cleanMessage = (msg, prefixToRemove) => {
    let cleaned = msg.startsWith(prefixToRemove) ? msg.substring(prefixToRemove.length).trim() : msg.trim();
    cleaned = cleaned.replace(/^question\s*\d+\)\s*/i, '').trim();
    return cleaned;
  };

  const feedback = {
      SYSTEM_PROMPT_INITIAL: "system prompt initial text",
      SYSTEM_PROMPT_NEXT_ITERATION: "system prompt next iteration text"
  };

  const shouldDisplaySystemMessage = (msg) => {
    const content = msg.replace('System:', '').trim().toLowerCase();
    return !(
      content.startsWith('you are ') ||
      content.startsWith('your task is') ||
      content.includes('format 1:') ||
      content.includes('format 2:') ||
      content.includes('decide if you now have enough information') ||
      content.includes('continue the process') ||
      content === feedback.SYSTEM_PROMPT_INITIAL.toLowerCase() ||
      content === feedback.SYSTEM_PROMPT_NEXT_ITERATION.toLowerCase()
    );
  };


  return (
    <div className={styles.conversationPageWrapper}>
      <div className={styles.chatWindow}>
        <div className={styles.header}>
          <h1>Getting On Track...</h1>
        </div>

        <div className={styles.conversation}>
          {conversation.map((msg, idx) => {
            if (msg.startsWith('User:')) {
              return (
                <div key={idx} className={styles.userBubble}>
                  <p>{cleanMessage(msg, 'User:')}</p>
                </div>
              );
            } else if (msg.startsWith('AI:')) {
              return (
                <div key={idx} className={styles.aiBubble}>
                  <p>{cleanMessage(msg, 'AI:')}</p>
                </div>
              );
            } else if (msg.startsWith('System:')) {
                if (shouldDisplaySystemMessage(msg)) {
                    return (
                        <div key={idx} className={styles.aiBubble}>
                        <p>{cleanMessage(msg, 'System:')}</p>
                        </div>
                    );
                }
            }
            return null;
          })}
          {isLoading && <div className={styles.loadingIndicator}><p>Thinking...</p></div>}
          <div ref={conversationEndRef} />
        </div>

        {isFinal ? (
          <div className={styles.savedMessage}>
            <p>Your final business problem has been saved for later use.</p>
          </div>
        ) : (
          <form className={styles.responseForm} onSubmit={handleSubmit}>
            <input
              type="text"
              placeholder="Type your answer here..."
              value={userResponse}
              onChange={e => setUserResponse(e.target.value)}
              required
              disabled={isLoading}
            />
            <button
              type="submit"
              className={styles.submitButton}
              disabled={isLoading || !userResponse.trim()}
            >
              {isLoading ? 'Sending...' : 'Send'}
            </button>
          </form>
        )}
      </div>
    </div>
  );
}
