import React, { useState, useRef, useEffect } from 'react';
import './index-react.css';
import { useNavigate } from 'react-router-dom';
import { IconButton } from '@mui/material';
import AttachFileIcon from '@mui/icons-material/AttachFile';
import MicIcon from '@mui/icons-material/Mic';
import { callApi } from '../api/client';

export default function Home() {
  const [queryInput, setQueryInput] = useState('');
  const [file, setFile] = useState(null);
  const navigate = useNavigate();
  const inputRef = useRef(null);
  const [theme] = useState('dark');

  const recognitionRef = useRef(null);
  const [isListening, setIsListening] = useState(false);
  const [micError, setMicError] = useState('');
  // New state to track if setup is complete and supported
  const [micReady, setMicReady] = useState(false);

  // Log initial ref state on render
  console.log('Render - Mic Ready:', micReady, 'Ref Current:', recognitionRef.current);

  useEffect(() => {
    console.log("Effect: Setting up SpeechRecognition...");
    const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;

    if (!SpeechRecognition) {
      console.warn("Speech Recognition not supported by this browser.");
      setMicError("Voice input not supported by this browser.");
      setMicReady(false); // Explicitly set not ready
      return;
    }

    // --- Permission Check (Optional but recommended) ---
    // Check for microphone permission *before* creating the object fully
    // Note: This API might not be supported in all browsers or might behave differently
    if (navigator.permissions && typeof navigator.permissions.query === 'function') {
        navigator.permissions.query({ name: 'microphone' })
            .then((permissionStatus) => {
                console.log('Mic permission status:', permissionStatus.state);
                if (permissionStatus.state === 'denied') {
                   setMicError("Microphone permission denied in browser settings.");
                   setMicReady(false);
                }
                // 'granted' or 'prompt' are usually okay to proceed
                permissionStatus.onchange = () => {
                    console.log('Mic permission status changed:', permissionStatus.state);
                    if (permissionStatus.state === 'denied') {
                         setMicError("Microphone permission denied.");
                         setMicReady(false);
                         // Maybe try to stop recognition if it was somehow running
                         recognitionRef.current?.stop();
                    } else {
                         setMicError(''); // Clear error if granted/prompted again
                         // Don't set micReady here, let initialization finish
                    }
                };
            })
            .catch(err => {
                console.error("Error querying mic permission:", err);
                // Proceed cautiously even if permission query fails
            });
    } else {
        console.warn("Permissions API not fully supported for microphone query.");
    }
     // --- End Permission Check ---


    const recognition = new SpeechRecognition();
    recognition.lang = "en-US";
    recognition.interimResults = false;
    recognition.maxAlternatives = 1;

    recognition.onresult = (event) => {
      const transcript = event.results[0][0].transcript;
      console.log('Speech Result:', transcript);
      setQueryInput(transcript);
      // 'onend' will handle setIsListening(false)
    };

    recognition.onerror = (event) => {
      console.error("Speech Recognition Error:", event.error, event.message);
      let errorMsg = `Speech error: ${event.error}`;
      if (event.error === 'not-allowed' || event.error === 'service-not-allowed') {
        errorMsg = "Microphone permission denied.";
      } else if (event.error === 'no-speech') {
        errorMsg = "No speech detected. Try speaking sooner.";
      } else if (event.error === 'audio-capture') {
          errorMsg = "Microphone error. Check hardware.";
      } else if (event.error === 'network') {
          errorMsg = "Network error during speech recognition.";
      }
      setMicError(errorMsg);
      setIsListening(false); // Ensure state is reset on error
    };

    recognition.onstart = () => {
      console.log('Speech Recognition Started');
      setIsListening(true);
      setMicError(''); // Clear previous errors on successful start
    };

    recognition.onend = () => {
      console.log('Speech Recognition Ended');
      // This ensures listening state is false after recognition stops for any reason
      setIsListening(false);
    };

    // Assign to ref *after* setup
    recognitionRef.current = recognition;
    setMicReady(true); // Mark setup as complete and supported
    console.log("Effect: Setup complete, Mic Ready:", true, "Ref:", recognitionRef.current);


    // Cleanup function
    return () => {
      if (recognitionRef.current) {
        recognitionRef.current.abort();
        console.log("Effect Cleanup: Speech recognition aborted.");
      }
    };

  }, []); // Empty dependency array: Run only once on mount

  const handleFileChange = (e) => {
    if (e.target.files && e.target.files[0]) {
      setFile(e.target.files[0]);
      setMicError(''); // Clear mic errors if file is selected
    } else {
      setFile(null);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (isListening && recognitionRef.current) {
      console.log("Form submit: Stopping active recognition.");
      recognitionRef.current.stop();
    }

    const formData = new FormData();
    formData.append('queryInput', queryInput);
    if (file) {
      formData.append('file', file);
    }

    console.log("Form submit: Sending data...");
    // Consider adding a loading state here
    try {
      const data = await callApi('home', 'POST', formData);
      if (data && data.conversation) {
        navigate('/conversation', { state: { conversation: data.conversation } });
      } else {
        console.error("API response missing data.");
        setMicError("Failed to process request.");
      }
    } catch (error) {
      console.error("API call failed:", error);
      setMicError("Network error or server issue.");
    }
    // Consider removing loading state here
  };

  const handleVoiceInput = () => {
    console.log(`handleVoiceInput: Clicked! MicReady=${micReady}, isListening=${isListening}`);

    if (!micReady || !recognitionRef.current) {
        console.warn("handleVoiceInput: Mic not ready or ref not set.");
        // Update error state just in case it wasn't set correctly
        if (!micError) setMicError("Voice input unavailable.");
        return; // Exit if not ready
    }

    if (!isListening) {
      try {
        setQueryInput(''); // Clear input before starting
        setFile(null);     // Clear file selection
        setMicError('');   // Clear previous errors
        console.log("handleVoiceInput: Calling recognition.start()...");
        recognitionRef.current.start();
        // State changes (isListening=true) are handled by 'onstart' listener
      } catch (error) {
        // Catch errors like "recognition already started"
        console.error("handleVoiceInput: Error caught trying to start:", error);
        setMicError("Could not start voice input (mic busy?).");
        // Ensure state is consistent if start fails immediately
        setIsListening(false);
      }
    } else {
      // If already listening, stop it
      console.log("handleVoiceInput: Calling recognition.stop()...");
      recognitionRef.current.stop();
      // State changes (isListening=false) are handled by 'onend' listener
    }
  };

  // Determine the final disabled state for the button
  const micButtonDisabled = !micReady;

  return (
    <div className={`autoML ${theme} ${isListening ? 'listening' : ''}`}>
      <div className="container">
        <div className="header">
          <p>Bonjour</p>
          <h1>How Can We Help You?</h1>
        </div>

        <form onSubmit={handleSubmit}>
          <div className="input-area">
            <div className="input-wrapper">
              <input
                type="text"
                ref={inputRef}
                value={queryInput}
                onChange={(e) => setQueryInput(e.target.value)}
                placeholder={isListening ? "Listening..." : "Describe Your Problem"}
                required
                disabled={isListening} // Only disable typing while actively listening
              />
              <div className="input-icons">
                <IconButton
                  component="label"
                  htmlFor="file-upload"
                  className="icon-button"
                  disabled={isListening} // Can still attach file unless listening
                  title="Attach file"
                >
                  <AttachFileIcon />
                </IconButton>
                <input
                  id="file-upload"
                  type="file"
                  hidden
                  onChange={handleFileChange}
                  disabled={isListening}
                />
                <IconButton
                  onClick={handleVoiceInput}
                  className={`icon-button ${isListening ? 'active' : ''}`}
                  // Disable based on overall readiness, not ref directly
                  disabled={micButtonDisabled}
                  title={
                    micError // Show error first
                      ? micError
                      : !micReady // Then show not ready/supported
                      ? "Voice input not supported or initializing"
                      : isListening // Then show listening state
                      ? "Stop Listening"
                      : "Start Voice Input"
                  }
                >
                  <MicIcon />
                </IconButton>
              </div>
            </div>

            {/* Display File Name */}
            {file && !micError && !isListening && ( // Hide file if error or listening
              <p className="file-display">
                File: {file.name}
              </p>
            )}

            {/* Display Listening Indicator or Error Message */}
            {isListening && (
              <p className="status-display listening-indicator">
                üéôÔ∏è Listening...
              </p>
            )}
            {micError && (
               <p className="status-display error-message">
                ‚ö†Ô∏è {micError}
              </p>
            )}
          </div>

          <button type="submit" className="action-button" disabled={isListening}>
            Start Analysis
          </button>
        </form>
      </div>
    </div>
  );
}
