import React, { useState, useRef, useEffect } from 'react';
import './index-react.css';
import { useNavigate } from 'react-router-dom';
import { IconButton } from '@mui/material';
import AttachFileIcon from '@mui/icons-material/AttachFile';
import MicIcon from '@mui/icons-material/Mic';
import { callApi } from '../api/client';

export default function Home() {
  const [queryInput, setQueryInput] = useState('');
  const [file, setFile] = useState(null);
  const navigate = useNavigate();
  const inputRef = useRef(null);
  const [theme] = useState('dark');

  const recognitionRef = useRef(null);
  const [isListening, setIsListening] = useState(false);
  const [micError, setMicError] = useState(''); // Optional: state to show mic errors

  useEffect(() => {
    const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;

    if (!SpeechRecognition) {
      console.warn("Speech Recognition not supported by this browser.");
      setMicError("Voice input not supported by this browser.");
      return; // Exit setup
    }

    const recognition = new SpeechRecognition();
    recognition.lang = "en-US";
    recognition.interimResults = false;
    recognition.maxAlternatives = 1;

    recognition.onresult = (event) => {
      const transcript = event.results[0][0].transcript;
      console.log('Speech Result:', transcript);
      setQueryInput(transcript);
      // Don't set isListening(false) here, let onend handle it
    };

    recognition.onerror = (event) => {
      console.error("Speech Recognition Error:", event.error, event.message);
      let errorMsg = `Speech error: ${event.error}`;
      if (event.error === 'not-allowed' || event.error === 'service-not-allowed') {
        errorMsg = "Microphone permission denied.";
      } else if (event.error === 'no-speech') {
        errorMsg = "No speech detected.";
      }
      setMicError(errorMsg);
      setIsListening(false); // Ensure state is reset on error
    };

    recognition.onstart = () => {
      console.log('Speech Recognition Started');
      setIsListening(true);
      setMicError(''); // Clear previous errors on successful start
    };

    recognition.onend = () => {
      console.log('Speech Recognition Ended');
      // This is the most reliable place to set listening to false,
      // as it fires after result, error, or manual stop.
      setIsListening(false);
    };

    recognitionRef.current = recognition;

    // Cleanup function
    return () => {
      if (recognitionRef.current) {
        recognitionRef.current.abort(); // Stop recognition if component unmounts
        console.log("Speech recognition aborted on unmount.");
      }
    };

  }, []); // Empty array: run only once on mount

  const handleFileChange = (e) => {
    if (e.target.files && e.target.files[0]) {
      setFile(e.target.files[0]);
      setMicError(''); // Clear mic errors if file is selected
    } else {
      setFile(null);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    // If recognition is somehow still active, stop it
    if (isListening && recognitionRef.current) {
      recognitionRef.current.stop();
    }

    const formData = new FormData();
    formData.append('queryInput', queryInput);
    if (file) {
      formData.append('file', file);
    }

    try {
      // Show loading state?
      const data = await callApi('home', 'POST', formData);
      // Hide loading state?
      if (data && data.conversation) {
        navigate('/conversation', { state: { conversation: data.conversation } });
      } else {
        console.error("API response missing data.");
        setMicError("Failed to process request."); // Show generic error
      }
    } catch (error) {
      // Hide loading state?
      console.error("API call failed:", error);
      setMicError("Network error or server issue."); // Show generic error
    }
  };

  const handleVoiceInput = () => {
    if (!recognitionRef.current) {
        console.log("Recognition ref not ready.");
        setMicError("Voice input not available.");
        return;
    }

    if (!isListening) {
      try {
        // Check permissions state (optional advanced)
        // navigator.permissions.query({ name: 'microphone' }).then(permissionStatus => { ... });
        setQueryInput(''); // Clear input before starting
        setFile(null); // Clear file selection maybe?
        setMicError(''); // Clear previous errors
        console.log("Attempting to start recognition...");
        recognitionRef.current.start();
        // State update (isListening=true) will be handled by the 'onstart' event
      } catch (error) {
        // This catch might trigger if start() is called while already active on some browsers
        console.error("Error caught trying to start recognition:", error);
        setMicError("Could not start voice input.");
        setIsListening(false); // Ensure state is false if start failed
      }
    } else {
      // If already listening, clicking the button should stop it
      console.log("Attempting to stop recognition...");
      recognitionRef.current.stop();
      // State update (isListening=false) will be handled by the 'onend' event
    }
  };

  return (
    <div className={`autoML ${theme} ${isListening ? 'listening' : ''}`}>
      <div className="container">
        <div className="header">
          <p>Bonjour</p>
          <h1>How Can We Help You?</h1>
        </div>

        <form onSubmit={handleSubmit}>
          <div className="input-area">
            <div className="input-wrapper">
              <input
                type="text"
                ref={inputRef}
                value={queryInput}
                onChange={(e) => setQueryInput(e.target.value)}
                placeholder={isListening ? "Listening..." : "Describe Your Problem"}
                required
                disabled={isListening}
              />
              <div className="input-icons">
                <IconButton
                  component="label"
                  htmlFor="file-upload"
                  className="icon-button"
                  disabled={isListening}
                  title="Attach file"
                >
                  <AttachFileIcon />
                </IconButton>
                <input
                  id="file-upload"
                  type="file"
                  hidden
                  onChange={handleFileChange}
                  disabled={isListening}
                />
                <IconButton
                  onClick={handleVoiceInput}
                  className={`icon-button ${isListening ? 'active' : ''}`}
                  disabled={!recognitionRef.current} // Disable if API wasn't set up
                  title={
                    micError // Show error in title if exists
                      ? micError
                      : recognitionRef.current // Check if ref exists before checking isListening
                      ? isListening
                        ? "Stop Listening"
                        : "Start Voice Input"
                      : "Voice Input not supported"
                  }
                >
                  <MicIcon />
                </IconButton>
              </div>
            </div>

            {/* Display File Name */}
            {file && !micError && ( // Don't show file if there's a mic error maybe?
              <p className="file-display">
                File: {file.name}
              </p>
            )}

            {/* Display Listening Indicator or Error Message */}
            {isListening && (
              <p className="status-display listening-indicator">
                üéôÔ∏è Listening...
              </p>
            )}
            {micError && (
               <p className="status-display error-message">
                ‚ö†Ô∏è {micError}
              </p>
            )}
          </div>

          <button type="submit" className="action-button" disabled={isListening}>
            Start Analysis
          </button>
        </form>
      </div>
    </div>
  );
}
