import React, { useState, useRef, useEffect } from 'react';
import './index-react.css'; // Keep your CSS import
import { useNavigate } from 'react-router-dom';
import { IconButton } from '@mui/material';
import AttachFileIcon from '@mui/icons-material/AttachFile';
import MicIcon from '@mui/icons-material/Mic';
import { callApi } from '../api/client';

export default function Home() {
  const [queryInput, setQueryInput] = useState('');
  const [file, setFile] = useState(null);
  const navigate = useNavigate();
  const inputRef = useRef(null);
  const [theme] = useState('dark'); // Assuming dark theme for now

  const recognitionRef = useRef(null);
  const [isListening, setIsListening] = useState(false);
  const [micError, setMicError] = useState('');
  const [micReady, setMicReady] = useState(false);

  // ... (useEffect and other functions remain the same) ...
  useEffect(() => {
    const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;

    if (!SpeechRecognition) {
      setMicError("Voice input not supported by this browser.");
      setMicReady(false);
      return;
    }

    if (navigator.permissions && typeof navigator.permissions.query === 'function') {
        navigator.permissions.query({ name: 'microphone' })
            .then((permissionStatus) => {
                if (permissionStatus.state === 'denied') {
                   setMicError("Microphone permission denied in browser settings.");
                   setMicReady(false);
                }
                permissionStatus.onchange = () => {
                    if (permissionStatus.state === 'denied') {
                         setMicError("Microphone permission denied.");
                         setMicReady(false);
                         recognitionRef.current?.stop();
                    } else {
                         setMicError('');
                    }
                };
            })
            .catch(err => {
                console.error("Error querying mic permission:", err);
            });
    } else {
        console.warn("Permissions API not fully supported for microphone query.");
    }


    const recognition = new SpeechRecognition();
    recognition.lang = "en-US";
    recognition.interimResults = false;
    recognition.maxAlternatives = 1;

    recognition.onresult = (event) => {
      const transcript = event.results[0][0].transcript;
      setQueryInput(transcript);
    };

    recognition.onerror = (event) => {
      let errorMsg = `Speech error: ${event.error}`;
      if (event.error === 'not-allowed' || event.error === 'service-not-allowed') {
        errorMsg = "Microphone permission denied.";
      } else if (event.error === 'no-speech') {
        errorMsg = "No speech detected. Try speaking sooner.";
      } else if (event.error === 'audio-capture') {
          errorMsg = "Microphone error. Check hardware.";
      } else if (event.error === 'network') {
          errorMsg = "Network error during speech recognition.";
      }
      setMicError(errorMsg);
      setIsListening(false);
    };

    recognition.onstart = () => {
      setIsListening(true);
      setMicError('');
    };

    recognition.onend = () => {
      setIsListening(false);
    };

    recognitionRef.current = recognition;
    setMicReady(true);


    return () => {
      if (recognitionRef.current) {
        recognitionRef.current.abort();
      }
    };

  }, []);

  const handleFileChange = (e) => {
    if (e.target.files && e.target.files[0]) {
      setFile(e.target.files[0]);
      setMicError('');
    } else {
      setFile(null);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (isListening && recognitionRef.current) {
      recognitionRef.current.stop();
    }

    const formData = new FormData();
    formData.append('queryInput', queryInput);
    if (file) {
      formData.append('file', file);
    }

    try {
      const data = await callApi('home', 'POST', formData);
      if (data && data.conversation) {
        navigate('/conversation', { state: { conversation: data.conversation } });
      } else {
        console.error("API response missing data.");
        setMicError("Failed to process request.");
      }
    } catch (error) {
      console.error("API call failed:", error);
      setMicError("Network error or server issue.");
    }
  };

  const handleVoiceInput = () => {
    if (!micReady || !recognitionRef.current) {
        if (!micError) setMicError("Voice input unavailable.");
        return;
    }

    if (!isListening) {
      try {
        setQueryInput('');
        setFile(null);
        setMicError('');
        recognitionRef.current.start();
      } catch (error) {
        console.error("handleVoiceInput: Error caught trying to start:", error);
        setMicError("Could not start voice input (mic busy?).");
        setIsListening(false);
      }
    } else {
      recognitionRef.current.stop();
    }
  };

  const micButtonDisabled = !micReady;

  // Define icon styles based on state
  const defaultIconColor = '#FFFFFF'; // White default
  const hoverIconColor = 'var(--accent)'; // Use CSS variable from :root
  const activeIconColor = '#ff4d4d'; // Red for active listening

  return (
    <div className={`autoML ${theme} ${isListening ? 'listening' : ''}`}>
      <div className="container">
        <div className="header">
          <p>Bonjour</p>
          <h1>How Can We Help You?</h1>
        </div>

        <form onSubmit={handleSubmit}>
          <div className="input-area">
            <div className="input-wrapper">
              <input
                type="text"
                ref={inputRef}
                value={queryInput}
                onChange={(e) => setQueryInput(e.target.value)}
                placeholder={isListening ? "Listening..." : "Describe Your Problem"}
                required
                disabled={isListening}
              />
              <div className="input-icons">
                {/* Attach File Icon */}
                <IconButton
                  component="label"
                  htmlFor="file-upload"
                  className="icon-button" // Keep class for non-color styles like size/padding
                  disabled={isListening}
                  title="Attach file"
                  sx={{ // Apply color via sx
                    color: defaultIconColor, // Default color
                    '&:hover': {
                      color: hoverIconColor, // Hover color
                    },
                  }}
                >
                  <AttachFileIcon />
                </IconButton>
                <input
                  id="file-upload"
                  type="file"
                  hidden
                  onChange={handleFileChange}
                  disabled={isListening}
                />
                {/* Mic Icon */}
                <IconButton
                  onClick={handleVoiceInput}
                  className={`icon-button ${isListening ? 'active' : ''}`} // Keep class for pulse animation etc.
                  disabled={micButtonDisabled}
                  title={
                    micError
                      ? micError
                      : !micReady
                      ? "Voice input not supported or initializing"
                      : isListening
                      ? "Stop Listening"
                      : "Start Voice Input"
                  }
                   sx={{ // Apply color via sx, conditionally for active state
                    color: isListening ? activeIconColor : defaultIconColor, // Active or Default color
                    '&:hover': {
                      // Only apply hover color if NOT active listening
                      color: isListening ? activeIconColor : hoverIconColor,
                    },
                  }}
                >
                  <MicIcon />
                </IconButton>
              </div>
            </div>

            {file && !micError && !isListening && (
              <p className="file-display">
                File: {file.name}
              </p>
            )}

            {isListening && (
              <p className="status-display listening-indicator">
                üéôÔ∏è Listening...
              </p>
            )}
            {micError && (
               <p className="status-display error-message">
                ‚ö†Ô∏è {micError}
              </p>
            )}
          </div>

          <button type="submit" className="action-button" disabled={isListening}>
            Start Analysis
          </button>
        </form>
      </div>
    </div>
  );
}
