import React, { useEffect, useState } from "react";
import { useLocation } from "react-router-dom";
import styles from "./conversation_style.module.css";

const Conversation = () => {
  const location = useLocation();
  const initialState = location.state || {};
  const [conversation, setConversation] = useState(initialState.conversation || []);
  const [userInput, setUserInput] = useState("");
  const [isFinal, setIsFinal] = useState(false);
  const [isLoading, setIsLoading] = useState(false);

  const callApi = async (endpoint, method = "GET", body = null) => {
    const config = {
      method,
      headers: { "Content-Type": "application/json" }
    };
    if (body) config.body = JSON.stringify(body);
    const res = await fetch(`/api/${endpoint}`, config);
    return await res.json();
  };

  useEffect(() => {
    const fetchInitialLLMResponse = async () => {
      // If only the user message + system prompt exist, get LLM's first question
      const onlyUserInput = conversation.length === 2 && conversation[0].startsWith("User:");

      if (onlyUserInput) {
        setIsLoading(true);
        try {
          const data = await callApi("conversation", "GET");
          if (data.conversation) {
            setConversation(data.conversation);
            if (data.final_problem) setIsFinal(true);
          }
        } catch (err) {
          console.error("Failed to get initial LLM question:", err);
        } finally {
          setIsLoading(false);
        }
      }
    };
    fetchInitialLLMResponse();
  }, [conversation]);

  const handleSubmit = async () => {
    if (!userInput.trim()) return;
    setIsLoading(true);
    try {
      const res = await callApi("conversation", "POST", {
        user_response: userInput
      });
      setConversation(res.conversation || []);
      setIsFinal(!!res.final_problem_determined);
      setUserInput("");
    } catch (err) {
      console.error("Submission error:", err);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className={styles.container}>
      <h2>Getting On Track...</h2>
      <div className={styles.chatBox}>
        {conversation.map((msg, idx) => {
          if (msg.startsWith("User:")) {
            return (
              <div key={idx} className={styles.userBubble}>
                <p>{msg.replace("User:", "").trim()}</p>
              </div>
            );
          } else if (msg.startsWith("AI:")) {
            return (
              <div key={idx} className={styles.aiBubble}>
                <p>{msg.replace("AI:", "").trim()}</p>
              </div>
            );
          }
          return null;
        })}
        {isLoading && (
          <div className={styles.aiBubble}>
            <p>Thinking...</p>
          </div>
        )}
      </div>
      {!isFinal && (
        <div className={styles.inputSection}>
          <input
            type="text"
            placeholder="Type your answer..."
            value={userInput}
            onChange={(e) => setUserInput(e.target.value)}
            disabled={isLoading}
          />
          <button onClick={handleSubmit} disabled={isLoading}>
            Send
          </button>
        </div>
      )}
      {isFinal && (
        <div className={styles.finalProblem}>
          <h3>âœ… Final Problem Defined</h3>
        </div>
      )}
    </div>
  );
};

export default Conversation;
