// In frontend/src/App.js (Replace entire file content)
import React, { useState, useRef, useEffect } from 'react';
import './index-react.css'; // Keep this import
import { IconButton } from '@mui/material'; // Keep needed MUI imports
import MicIcon from '@mui/icons-material/Mic';
import AttachFileIcon from '@mui/icons-material/AttachFile';
import DarkModeIcon from '@mui/icons-material/DarkMode';
import LightModeIcon from '@mui/icons-material/LightMode';

// This is the component definition previously inside Index.jsx
const AutoMLAssistant = () => {
  const [theme, setTheme] = useState('dark');
  const [query, setQuery] = useState('');
  const inputRef = useRef(null);
  const recognitionRef = useRef(null);

  useEffect(() => {
    const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
    if (SpeechRecognition) {
      const recognition = new SpeechRecognition();
      recognition.lang = 'en-US';
      recognition.interimResults = false;
      recognition.maxAlternatives = 1;
      recognition.onresult = (event) => {
        const transcript = event.results[0][0].transcript;
        setQuery(transcript);
      };
      recognitionRef.current = recognition;
    }
  }, []);

  const toggleTheme = () => {
    setTheme(theme === 'dark' ? 'light' : 'dark');
  };

  const startVoiceInput = () => {
    if (recognitionRef.current) recognitionRef.current.start();
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Query:', query);
    // Add your actual submission logic here
  };

  return (
    <div className={`autoML ${theme}`}>
      <div className="background-animation" />

      <IconButton className="theme-toggle" onClick={toggleTheme}>
        {theme === 'dark' ? <DarkModeIcon /> : <LightModeIcon />}
      </IconButton>

      <div className="container">
        <div className="header">
          <p>Bonjour.</p>
          <h1>How can I help you today?</h1>
        </div>

        <form onSubmit={handleSubmit}>
          <div className="input-area">
            <div className="input-wrapper tilt">
              <input
                type="text"
                value={query}
                ref={inputRef}
                placeholder="What do you want to know?"
                onChange={(e) => setQuery(e.target.value)}
              />
              <div className="input-icons">
                <IconButton component="label" htmlFor="file-upload" className="icon-button">
                  <AttachFileIcon />
                </IconButton>
                <input type="file" id="file-upload" hidden />
                <IconButton onClick={startVoiceInput} className="icon-button">
                  <MicIcon />
                </IconButton>
              </div>
            </div>
          </div>
          <button type="submit" className="action-button tilt">Let's GO</button>
        </form>
      </div>
    </div>
  );
};

export default AutoMLAssistant;
