import React, { useEffect, useState } from 'react';
import styles from './chat.module.css';

export default function Chat() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [status, setStatus] = useState('loading'); // 'loading' | 'error' | 'ready'

  // On mount, initialize chat
  useEffect(() => {
    (async () => {
      try {
        const res = await fetch('/api/chatlm', { method: 'POST' });
        if (!res.ok) throw new Error(`HTTP ${res.status}`);
        const { suggestion } = await res.json();
        setMessages([{ role: 'assistant', content: suggestion }]);
        setStatus('ready');
      } catch (err) {
        console.error('Chat init failed:', err);
        setStatus('error');
      }
    })();
  }, []);

  // Send a user message and fetch assistant reply
  const sendMessage = async () => {
    const text = input.trim();
    if (!text) return;
    // add user bubble
    setMessages(m => [...m, { role: 'user', content: text }]);
    setInput('');
    setStatus('loading');

    try {
      const res = await fetch('/api/chatlm/message', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ user_message: text }),
      });
      if (!res.ok) throw new Error(await res.text());
      const { response } = await res.json();
      setMessages(m => [...m, { role: 'assistant', content: response }]);
      setStatus('ready');
    } catch (err) {
      console.error('Chat send failed:', err);
      setStatus('error');
    }
  };

  if (status === 'loading') {
    return <p className={styles.loading}>Loading chat…</p>;
  }
  if (status === 'error') {
    return <p className={styles.error}>Unable to load chat.</p>;
  }

  return (
    <div className={styles.chatContainer}>
      <div className={styles.messageList}>
        {messages.map((m, i) => (
          <div
            key={i}
            className={m.role === 'user' ? styles.userMsg : styles.botMsg}
          >
            {m.content}
          </div>
        ))}
      </div>
      <div className={styles.inputBar}>
        <input
          className={styles.input}
          type="text"
          value={input}
          onChange={e => setInput(e.target.value)}
          onKeyDown={e => e.key === 'Enter' && sendMessage()}
          placeholder="Type your message…"
        />
        <button
          className={styles.sendBtn}
          onClick={sendMessage}
          disabled={status === 'loading'}
        >
          Send
        </button>
      </div>
    </div>
  );
}
